$darkgray: #4a4a4a;
$lightgray: #f8f8f9;
$coolgray: #d8d8d8;
$darkblue: hsl(209, 48%, 17%);
$primary: #32608f;
$secondary: #4483c1;
$lightblue: #e4edf6;
$darkred: #e15924;
$lightred: #c16744;
$yellow: #fec800;
$lightyellow: #fff3c7;
$green: #30ab6e;

@mixin gutters() {
	padding-left: 5vw;
	padding-right: 5vw;
	@media screen and (min-width: 768px) {
		padding-left: 2.5vw;
		padding-right: 2.5vw;
	}
}

@mixin outer_gutters() {
	padding-left: 5vw;
	padding-right: 5vw;
	@media screen and (min-width: 768px) {
		padding-left: 2.5vw;
		padding-right: 2.5vw;
	}
	@media screen and (min-width: 1441px) {
		padding-left: 7.5vw;
		padding-right: 7.5vw;
	}
}

@mixin drop-shadow($shade: "dark") {
	@if $shade==dark {
		box-shadow: 0 3px 4px 0 rgba(0, 0, 0, 0.25);
	}

	@if $shade==light {
		box-shadow: 0 2px 3px 0 rgba(0, 0, 0, 0.08);
	}
}

@mixin serif() {
	font-family: "PT Serif", serif;
}

@mixin sans-serif() {
	font-family: "Fira Sans", sans-serif;
}

@mixin external-links() {
	a[href^="http:"]:after,
	a[href^="https:"]:after {
		content: "\f35d";
		font-family: "Font Awesome 5 Free";
		margin-left: 0.35rem;
		font-size: 0.75em;
		opacity: 0.4;
		font-weight: 600;
		position: relative;
		top: -1px;
	}
}

@mixin text-shadow() {
	text-shadow: 2px 1px 1px rgba(0, 0, 0, 0.45);
}

@mixin blur {
	color: transparent;
	text-shadow: 0 0 15px #000;
}

@mixin truncate {
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}

@mixin form-control {
	display: block;
	width: 100%;
	height: calc(2.25rem + 2px);
	padding: 0.375rem 0.75rem;
	font-size: 1rem;
	line-height: 1.5;
	color: #495057;
	background-color: #fff;
	background-clip: padding-box;
	border: 1px solid lighten($coolgray, 5%);
	border-radius: 0.25rem;
	transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
	font-weight: normal;
	@include sans-serif();
}

@mixin white-data-pattern() {
	background-color: #ffffff;
	background-image: url("../assets/patterns/dot-grid.png");
	background-attachment: inherit;
	background-size: 300px;
}

