@mixin outer_gutters() {
	padding-left: 5vw;
	padding-right: 5vw;
	@media screen and (min-width: 768px) {
		padding-left: 2.5vw;
		padding-right: 2.5vw;
	}
	@media screen and (min-width: 1441px) {
		padding-left: 7.5vw;
		padding-right: 7.5vw;
	}
